mapper.update:

UPDATE T_ITEMS SET 
	IS_PREF = #{IS_PREF},
	LAST_UPDATE = #{LAST_UPDATE}
WHERE ITEM_ID = #{ITEM_ID}

mapper.select:
SELECT * FROM T_ITEMS
WHERE STATUS = '30'

		AND ITEM_ID = #{ITEM_ID, jdbcType=NUMERIC}  


		AND ITEM_NAME LIKE CONCAT(CONCAT('%', #{ITEM_NAME, jdbcType=VARCHAR}),'%') 


		AND ITEM_SHORT_NAME LIKE CONCAT(CONCAT('%', #{ITEM_SHORT_NAME}),'%') 


		AND MERCHANT_ID = #{MERCHANT_ID} 


		AND CATEGORY_ID = #{CATEGORY_ID} 


		AND IS_PREF = #{IS_PREF} 


		AND IS_SKILL = #{IS_SKILL} 


		AND IS_POINT = #{IS_POINT} 


		AND ITEM_DESC LIKE CONCAT(CONCAT('%', #{ITEM_DESC}),'%')

SELECT COUNT(*) FROM T_ITEMS A,T_MERCHANTS B
WHERE A.ITEM_ID = #{ITEM_ID}
AND A.MERCHANT_ID = B.MERCHANT_ID
AND A.MERCHANT_ID = #{MERCHANT_ID}
AND B.MERCHANT_ID = #{MERCHANT_ID}
AND B.USER_ID = #{USER_ID}
AND A.STATUS = #{STATUS}

SELECT COUNT(*) FROM T_ITEMS 
WHERE MERCHANT_ID = #{MERCHANT_ID} 
AND STATUS != '50'

SELECT COUNT(*) FROM T_USER_ITEM_COLL 
WHERE USER_ID = #{USER_ID} 
AND ITEM_ID = #{ITEM_ID}

SELECT INVENTORY,QTY_SOLD FROM T_ITEMS 
WHERE ITEM_ID = #{ITEM_ID} 
AND STATUS = '30'
